#!/bin/bash

gcloud beta compute --project=brightkey-project instances create us-east1 --zone=us-east1-b --machine-type=f1-micro --subnet=us-east1 --network-tier=PREMIUM --metadata=startup-script=curl\ -sSO\ https://dl.google.com/cloudagents/install-monitoring-agent.sh$'\n'sudo\ bash\ install-monitoring-agent.sh$'\n'curl\ -sSO\ https://dl.google.com/cloudagents/install-logging-agent.sh$'\n'sudo\ bash\ install-logging-agent.sh --maintenance-policy=MIGRATE --service-account=1040975251580-compute@developer.gserviceaccount.com --scopes=https://www.googleapis.com/auth/devstorage.read_only,https://www.googleapis.com/auth/logging.write,https://www.googleapis.com/auth/monitoring.write,https://www.googleapis.com/auth/servicecontrol,https://www.googleapis.com/auth/service.management.readonly,https://www.googleapis.com/auth/trace.append --tags=ssh,http-server --image=ubuntu-1604-xenial-v20200223 --image-project=ubuntu-os-cloud --boot-disk-size=10GB --boot-disk-type=pd-standard --boot-disk-device-name=us-east1 --no-shielded-secure-boot --shielded-vtpm --shielded-integrity-monitoring --reservation-affinity=any

gcloud beta compute --project=brightkey-project instances create us-west1 --zone=us-west1-b --machine-type=f1-micro --subnet=us-west1 --network-tier=PREMIUM --metadata=startup-script=curl\ -sSO\ https://dl.google.com/cloudagents/install-monitoring-agent.sh$'\n'sudo\ bash\ install-monitoring-agent.sh$'\n'curl\ -sSO\ https://dl.google.com/cloudagents/install-logging-agent.sh$'\n'sudo\ bash\ install-logging-agent.sh --maintenance-policy=MIGRATE --service-account=1040975251580-compute@developer.gserviceaccount.com --scopes=https://www.googleapis.com/auth/devstorage.read_only,https://www.googleapis.com/auth/logging.write,https://www.googleapis.com/auth/monitoring.write,https://www.googleapis.com/auth/servicecontrol,https://www.googleapis.com/auth/service.management.readonly,https://www.googleapis.com/auth/trace.append --tags=ssh,http-server --image=ubuntu-1604-xenial-v20200223 --image-project=ubuntu-os-cloud --boot-disk-size=10GB --boot-disk-type=pd-standard --boot-disk-device-name=us-west1 --no-shielded-secure-boot --shielded-vtpm --shielded-integrity-monitoring --reservation-affinity=any

gcloud compute --project=brightkey-project firewall-rules create demo-sdk-vpc-allow-http --direction=INGRESS --priority=1000 --network=demo-sdk-vpc --action=ALLOW --rules=tcp:80 --source-ranges=0.0.0.0/0 --target-tags=http-server

